INSERT INTO extension_lines (number, name, operation) VALUES
    ( 1, "2006 – Alfabetização, leitura e escrita"                                  , "Alfabetização e letramento de crianças, jovens e adultos; formação do leitor e do produtor de textos; incentivo à leitura; literatura; desenvolvimento de metodologias de ensino da leitura e da escrita e sua inclusão nos projetos político-pedagógicos das escolas."),
    ( 2, "2006 – Artes cênicas"                                                     , "Dança, teatro, técnicas circenses, performance; formação, capacitação e qualificação de pessoas que atuam na área; memória, produção e difusão cultural e artística."),
    ( 3, "2006 – Artes integradas"                                                  , "Ações multiculturais, envolvendo as diversas áreas da produção e da prática artística em um único programa integrado; memória, produção e difusão cultural e artística."),
    ( 4, "2006 – Artes plásticas"                                                   , "Escultura, pintura, desenho, gravura, instalação, apropriação; formação, memória, produção e difusão cultural e artística."),
    ( 5, "2006 – Artes visuais"                                                     , "Artes gráficas, fotografia, cinema, vídeo; memória, produção e difusão cultural e artística."),
    ( 6, "2006 – Comunicação estratégica"                                           , "Elaboração, implementação e avaliação de planos estratégicos de comunicação; realização de assessorias e consultorias para organizações de natureza diversa em atividades de publicidade, propaganda e de relações públicas; suporte de comunicação a programas e projetos de mobilização social, a organizações governamentais e da sociedade civil."),
    ( 7, "2006 – Desenvolvimento de produtos"                                       , "Produção de origem animal, vegetal, mineral e laboratorial; manejo, transformação, manipulação, dispensação, conservação e comercialização de produtos e subprodutos."),
    ( 8, "2006 – Desenvolvimento regional"                                          , "Elaboração de diagnóstico e de propostas de planejamento regional (urbano e rural) envolvendo práticas destinadas à elaboração de planos diretores, a soluções, tratamento de problemas e melhoria da qualidade de vida da população local, tendo em vista sua capacidade produtiva e potencial de incorporação na implementação das ações; participação em fóruns Desenvolvimento Local Integrado e Sustentável – DLIS; participação e assessoria a conselhos regionais, estaduais e locais de desenvolvimento e a fóruns de municípios e associações afins; elaboração de matrizes e estudos sobre desenvolvimento regional integrado, tendo como base recursos locais renováveis e práticas sustentáveis; permacultura; definição de indicadores e métodos de avaliação de desenvolvimento, crescimento e sustentabilidade."),
    ( 9, "2006 – Desenvolvimento rural e questão agrária"                           , "Constituição e/ou implementação de iniciativas de reforma agrária, matrizes produtivas locais ou regionais e de políticas de desenvolvimento rural; assistência técnica; planejamento do desenvolvimento rural sustentável; organização rural; comercialização; agroindústria; gestão de propriedades e/ou organizações; arbitragem de conflitos de reforma agrária; educação para o desenvolvimento rural; definição de critérios e de políticas de fomento para o meio rural; avaliação de impactos de políticas de desenvolvimento rural."),
    (10, "2006 – Desenvolvimento tecnológico"                                       , "Processos de investigação e produção de novas tecnologias, técnicas, processos produtivos, padrões de consumo e produção (inclusive tecnologias sociais, práticas e protocolos de produção de bens e serviços); serviços tecnológicos; estudos de viabilidade técnica, financeira e econômica; adaptação de tecnologias."),
    (11, "2006 – Desenvolvimento urbano"                                            , "Planejamento, implementação e avaliação de processos e metodologias visando proporcionar soluções e o tratamento de problemas das comunidades urbanas; urbanismo."),
    (12, "2006 – Direitos individuais e coletivos"                                  , "Apoio a organizações e ações de memória social, defesa, proteção e promoção de direitos humanos; direito agrário e fundiário; assistência jurídica e judiciária, individual e coletiva, a instituições e organizações; bioética médica e jurídica; ações educativas e preventivas para garantia de direitos humanos."),
    (13, "2006 – Educação profissional"                                             , "Formação técnica profissional, visando a valorização, aperfeiçoamento, promoção do acesso aos direitos trabalhistas e inserção no mercado de trabalho."),
    (14, "2006 – Empreendedorismo"                                                  , "Constituição e gestão de empresas juniores, pré-incubadoras, incubadoras de empresas, parques e pólos tecnológicos, cooperativas e empreendimentos solidários e outras ações voltadas para a identificação, aproveitamento de novas oportunidades e recursos de maneira inovadora, com foco na criação de empregos e negócios, estimulando a pró-atividade."),
    (15, "2006 – Emprego e renda"                                                   , "Defesa, proteção, promoção e apoio a oportunidades de trabalho, emprego e renda para empreendedores, setor informal, proprietários rurais, formas cooperadas/associadas de produção, empreendimentos produtivos solidários, economia solidária, agricultura familiar, dentre outros."),
    (16, "2006 – Endemias e epidemias"                                              , "Planejamento, implementação e avaliação de metodologias de intervenção e de investigação tendo como tema o perfil epidemiológico de endemias e epidemias e a transmissão de doenças no meio rural e urbano; previsão e prevenção."),
    (17, "2006 – Espaços de ciência"                                                , "Difusão e divulgação de conhecimentos científicos e tecnológicos em espaços de ciência, como museus, observatórios, planetários, estações marinhas, entre outros; organização desses espaços."),
    (18, "2006 – Esporte e lazer"                                                   , "Práticas esportivas, experiências culturais, atividades físicas e vivências de lazer para crianças, jovens e adultos, como princípios de cidadania, inclusão, participação social e promoção da saúde; esportes e lazer nos projetos políticopedagógico das escolas; desenvolvimento de metodologias e inovações pedagógicas no ensino da Educação Física, Esportes e Lazer; iniciação e prática esportiva; detecção e fomento de talentos esportivos."),
    (19, "2006 – Estilismo"                                                         , "Estilismo e moda."), 
    (20, "2006 – Fármacos e medicamentos"                                           , "Uso correto de medicamentos para a assistência à saúde, em seus processos que envolvem a farmacoterapia; farmácia nuclear; diagnóstico laboratorial; análises químicas, físico-químicas, biológicas, microbiológicas e toxicológicas de fármacos, insumos farmacêuticos, medicamentos e fitoterápicos."),
    (21, "2006 – Formação de professores (formação docente)"                        , "Formação e valorização de professores, envolvendo a discussão de fundamentos e estratégias para a organização do trabalho pedagógico, tendo em vista o aprimoramento profissional, a valorização, a garantia de direitos trabalhistas e a inclusão no mercado de trabalho formal."),
    (22, "2006 – Gestão do trabalho"                                                , "Estratégias de administração; ambiente empresarial; relações de trabalho urbano e rural (formas associadas de produção, trabalho informal, incubadora de cooperativas populares, agronegócios, agroindústria, práticas e produções caseiras, dentre outros)."),
    (23, "2006 – Gestão informacional"                                              , "Sistemas de fornecimento e divulgação de informações econômicas, financeiras, físicas e sociais das instituições públicas, privadas e do terceiro setor."),
    (24, "2006 – Gestão institucional"                                              , "Estratégias administrativas e organizacionais em órgãos e instituições públicas, privadas e do terceiro setor, governamentais e não-governamentais."),
    (25, "2006 – Gestão pública"                                                    , "Sistemas regionais e locais de políticas públicas; análise do impacto dos fatores sociais, econômicos e demográficos nas políticas públicas (movimentos populacionais, geográficos e econômicos, setores produtivos); formação, capacitação e qualificação de pessoas que atuam nos sistemas públicos (atuais ou potenciais)."),
    (26, "2006 – Grupos sociais vulneráveis"                                        , "Questões de gênero, de etnia, de orientação sexual, de diversidade cultural, de credos religiosos, dentre outros, processos de atenção (educação, saúde, assistência social, etc), de emancipação, de respeito à identidade e inclusão; promoção, defesa e garantia de direitos; desenvolvimento de metodologias de intervenção."),
    (27, "2006 – Infância e adolescência"                                           , "Processos de atenção (educação, saúde, assistência social, etc), promoção, defesa e garantia de direitos; ações especiais de prevenção e erradicação do trabalho infantil; desenvolvimento de metodologias de intervenção tendo como objeto enfocado na ação crianças, adolescentes e suas famílias."),
    (28, "2006 – Inovação tecnológica"                                              , "Introdução de produtos ou processos tecnologicamente novos e melhorias significativas a serem implementadas em produtos ou processos existentes nas diversas áreas do conhecimento; considera-se uma inovação tecnológica de produto ou processo aquela que tenha sido implementada e introduzida no mercado (inovação de produto) ou utilizada no processo de produção (inovação de processo)."),
    (29, "2006 – Jornalismo"                                                        , "Processos de produção e edição de notícias para mídias impressas e eletrônicas; assessorias e consultorias para órgãos de imprensa em geral; crítica de mídia."),
    (30, "2006 – Jovens e adultos"                                                  , "Processos de atenção (saúde, assistência social, etc), emancipação e inclusão; educação formal e não formal; promoção, defesa e garantia de direitos; desenvolvimento de metodologias de intervenção, tendo como objeto a juventude e/ou a idade adulta."),
    (31, "2006 – Línguas estrangeiras"                                              , "Processos de ensino/aprendizagem de línguas estrangeiras e sua inclusão nos projetos político-pedagógicos das escolas; desenvolvimento de processos de formação em línguas estrangeiras; literatura; tradução."),
    (32, "2006 – Metodologias e estratégias de ensino/aprendizagem"                 , "Metodologias e estratégias específicas de ensino/aprendizagem, como a educação a distância, o ensino presencial e de pedagogia de formação inicial, educação continuada, educação permanente e formação profissional."),
    (33, "Mídias-artes"                                                             , "Mídias contemporâneas, multimídia, web-arte, arte digital."),
    (34, "Mídias"                                                                   , "Veículos comunitários e universitários, impressos e eletrônicos (boletins, rádio, televisão, jornal, revistas,internet, etc); promoção do uso didático dos meios de educação e de ações educativas sobre as mídias."),
    (35, "2006 – Música"                                                            , "Apreciação, criação e performance; formação, capacitação e qualificação de pessoas que atuam na área musical; produção e divulgação de informações, conhecimentos e material didático na área; memória, produção e difusão cultural e artística."),
    (36, "2006 – Organizações da sociedade civil e movimentos sociais e populares"  , "Apoio à formação, organização e desenvolvimento de comitês, comissões, fóruns, associações, ONG’s, OSCIP’s, redes, cooperativas populares, sindicatos, dentre outros."),
    (37, "2006 – Patrimônio cultural, histórico, natural e imaterial"               , "Preservação, recuperação, promoção e difusão de patrimônio artístico, cultural e histórico (bens culturais móveis e imóveis, obras de arte, arquitetura, espaço urbano, paisagismo, música, literatura, teatro, dança, artesanato, folclore, manifestações religiosas populares), natural (natureza, meio ambiente) material e imaterial (culinária, costumes do povo), mediante formação, organização, manutenção, ampliação e equipamento de museus, bibliotecas, centros culturais, arquivos e outras organizações culturais, coleções e acervos; restauração de bens móveis e imóveis de reconhecido valor cultural; proteção e promoção do folclore, do artesanato, das tradições culturais e dos movimentos religiosos populares; valorização do patrimônio; memória, produção e difusão cultural e artística."),
    (38, "2006 – Pessoas com deficiências, incapacidades, e necessidades especiais" , "Processos de atenção (educação, saúde, assistência social, etc), de emancipação e inclusão de pessoas com deficiências, incapacidades físicas, sensoriais e mentais, síndromes, doenças crônicas, altas habilidades, dentre outras; promoção, defesa e garantia de direitos; desenvolvimento de metodologias de intervenção individual e coletiva, tendo como objeto enfocado na ação essas pessoas e suas famílias."),
    (39, "2006 – Propriedade intelectual e patente"                                 , "Processos de identificação, regulamentação e registrode direitos autorais e sobre propriedade intelectual epatente."),
    (40, "2006 – Questões ambientais"                                               , "Implementação e avaliação de processos de educação ambiental de redução da poluição do ar, águas e solo; discussão da Agenda 21; discussão de impactos ambientais de empreendimentos e de planos básicos ambientais; preservação de recursos naturais e planejamento ambiental; questões florestais; meio ambiente e qualidade de vida; cidadania e meio ambiente."),
    (41, "2006 – Recursos hídricos"                                                 , "Planejamento de microbacias, preservação de mata ciliar e dos recursos hídricos, gerenciamento de recursos hídricos e bacias hidrográficas; prevenção e controle da poluição; arbitragem de conflitos; participação em agências e comitês estaduais e nacionais; assessoria técnica a conselhos estaduais, comitês e consórcios municipais de recursos hídricos."),
    (42, "2006 – Resíduos sólidos"                                                  , "Orientação para desenvolvimento de ações normativas, operacionais, financeiras e de planejamento com base em critérios sanitários, ambientais e econômicos, para coletar, segregar, tratar e dispor o lixo; orientação para elaboração e desenvolvimento de projetos de planos de gestão integrada de resíduos sólidos urbanos, coleta seletiva, instalação de manejo de resíduos sólidos urbanos reaproveitáveis (compostagem e reciclagem), destinação final (aterros sanitários e controlados), e remediação de resíduos a céu aberto; orientação à organização de catadores de lixo."),
    (43, "2006 – Saúde animal"                                                      , "Processos e metodologias visando a assistência à saúde animal: prevenção, diagnóstico e tratamento; prestação de serviços institucionais em laboratórios, clínicas e hospitais veterinários universitários."),
    (44, "2006 – Saúde da família"                                                  , "Processos assistenciais e metodologias de intervenção para a saúde da família."),
    (45, "2006 – Saúde e proteção no trabalho"                                      , "Processos assistenciais, metodologias de intervenção, ergonomia, educação para a saúde e vigilância epidemiológica ambiental, tendo como alvo o ambiente de trabalho e como público os trabalhadores urbanos e rurais; saúde ocupacional."),
    (46, "2006 – Saúde humana"                                                      , "Promoção da saúde das pessoas, famílias e comunidades;    humanização dos serviços; prestação de serviços institucionais  em ambulatórios, laboratórios, clínicas e hospitais   universitários; assistência à saúde de pessoas em serviços especializados de diagnóstico, análises clínicas e tratamento;   clínicas odontológicas, de psicologia, dentre outras."),
    (47, "2006 – Segurança alimentar e nutricional"                                 , "Incentivo à produção de alimentos básicos, auto-abastecimento, agricultura urbana, hortas escolares e comunitárias, nutrição, educação para o consumo, regulação do mercado de alimentos, promoção e defesa do consumo alimentar."),
    (48, "2006 – Segurança pública e defesa social"                                 , "Planejamento, implementação e avaliação de processos e metodologias, dentro de uma compreensão global do conceito de segurança pública, visando proporcionar soluções e tratamento de problemas relacionados; orientação e assistência jurídica, judiciária, psicológica e social à população carcerária e seus familiares; assessoria a projetos de educação, saúde e trabalho aos apenados e familiares; questão penitenciária; violência; mediação de conflitos; atenção a vítimas de crimes violentos; proteção a testemunhas; policiamento comunitário."),
    (49, "2006 – Tecnologia da informação"                                          , "Desenvolvimento de competência informacional para identificar, localizar, interpretar, relacionar, analisar, sintetizar, avaliar e comunicar informação em fontes impressas ou eletrônicas; inclusão digital."),
    (50, "2006 – Temas específicos / Desenvolvimento humano"                        , "Temas das diversas áreas do conhecimento, especialmente de ciências humanas, biológicas, sociais aplicadas, exatas e da terra, da saúde, ciências agrárias, engenharias, lingüística, (letras e artes), visando a reflexão, discussão, atualização e aperfeiçoamento humano."),
    (51, "2006 – Terceira idade"                                                    , "Planejamento, implementação e avaliação de processos de atenção (educação, saúde, assistência social, etc), de emancipação e inclusão; promoção, defesa e garantia de direitos; desenvolvimento de metodologias de intervenção, tendo como objeto enfocado na ação pessoas idosas e suas famílias."),
    (52, "2006 – Turismo"                                                           , "Planejamento e implementação do turismo (ecológico, cultural, de lazer, de negócios, religioso, etc) como setor gerador de emprego e renda; desenvolvimento de novas tecnologias para avaliações de potencial turístico; produção e divulgação de imagens em acordo com as especificidades culturais das populações locais."),
    (53, "2006 – Uso de drogas e dependência química"                               , "Prevenção e limitação da incidência e do consumo de drogas; tratamento de dependentes; assistência e orientação a usuários de drogas; recuperação e reintegração social.");
